package holamundo;

public class TablaDeMultiplicar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int numero = 7;
		
		for (int i = 1; i < 11; i++) {
			int resultado = numero * i;
			System.out.println(numero + "X" + i + "= " + resultado );
		}
		
		

	}

}
